import React from 'react';
import Confetti from 'react-confetti';
function Card({ content, isFlipped, isMatched, onClick, cardSize, gameEnded, id }) {
    // Generate a random rotation angle for each card
    const rotationAngle = Math.random() * 230 - 115;

    // Define the keyframes for the rotateAndMoveDown animation
    const rotateAndMoveDownKeyframes = `
        @keyframes rotateAndMoveDown {
          0% {
            transform: rotate(${rotationAngle}deg) translateY(0);
          }
          100% {
            transform: rotate(${rotationAngle}deg) translateY(150vh);
          }
        }
    `;

    // Dynamically create a style element to insert the keyframes
    const style = document.createElement('style');
    style.innerHTML = rotateAndMoveDownKeyframes;
    document.head.appendChild(style);

    return (
        <div
            className={`group rounded-xl [perspective:1000px]`}
            onClick={() => !isMatched && onClick()}
            style={{
                height: `${cardSize}px`,
                width: `${cardSize}px`,
                opacity: isMatched ? 0.5 : 1,
                transition: 'opacity 0.25s ease-in',
            }}
        >
            <div
                className={`hover:scale-105 cursor-pointer relative h-full w-full rounded-xl shadow-xl transition-all duration-500 text-slate-200 [transform-style:preserve-3d] [backface-visibility:hidden] ${
                    isFlipped ? '[transform:rotateY(180deg)]' : ''
                } ${
                    gameEnded ? 'animate-rotateAndMoveDown' : '' // Apply the animation only when the game is lost and the card is flipped but not matched
                }`}
            >
                <div className='absolute inset-0 flex rounded-xl bg-slate-600 justify-center items-center align-center'>
                    {isFlipped ? <img className='w-full h-full rounded-xl object-cover bg-cover' src={content} alt="Card Front"></img> : ''}
                </div>
                <div
                    className={`absolute inset-0 h-full w-full rounded-xl bg-slate-600 flex justify-center items-center align-center text-slate-200 [transform:rotateY(180deg)] [backface-visibility:hidden] `}
                >
                    {isFlipped && <img className='w-full h-full rounded-xl object-cover bg-cover' src={content} alt="Card Back"></img>}
                </div>
            </div>
        </div>
    );
}

export default Card;
