import React, { useEffect, useRef } from 'react';
import * as THREE from 'three';
import VANTA from 'vantajs';

const VantaBackground = ({ effect }) => {
  const vantaRef = useRef(null);
  const effectInstance = useRef(null);

  useEffect(() => {
    // Ensure the effect is valid before initializing
    if (!effect || !VANTA[effect]) {
      console.error(`Invalid Vanta.js effect: ${effect}`);
      return;
    }

    // Initialize Vanta.js background effect based on the selected effect
    effectInstance.current = VANTA[effect]({
      el: vantaRef.current,
      THREE: THREE
      // Additional options for the effect can be added here
    });

    // Cleanup function
    return () => {
      if (effectInstance.current) {
        effectInstance.current.destroy();
      }
    };
  }, [effect]);

  return <div ref={vantaRef} style={{ width: '100%', height: '100%' }} />;
};

export default VantaBackground;
