import React, { useEffect } from 'react';

cardSize = Math.min(window.innerWidth * 0.11, 100);
function Card({ content, isFlipped, isMatched, onClick, cardSize}) {

    return (
        <>
        <div
            className={`group rounded-xl [perspective:1000px]`}
            onClick={() => !isMatched && onClick()}
            style={{
            height: `${cardSize}px`,
            width: `${cardSize}px`,
            opacity: isMatched ? 0.5 : 1,
            transition: 'opacity 0.25s ease-in', // Decrease opacity if matched
            }}
        >
            <div className={`relative h-full w-full rounded-xl shadow-xl transition-all duration-500 text-slate-200 [transform-style:preserve-3d] [backface-visibility:hidden] ${isFlipped ? '[transform:rotateY(180deg)]' : ''}`}>
            <div className='absolute inset-0 flex rounded-xl bg-red-500 justify-center items-center align-center'>
                {isFlipped ? content : ''}
            </div>
            <div className={`absolute inset-0 h-full w-full rounded-xl bg-red-500 flex justify-center items-center align-center text-slate-200 [transform:rotateY(180deg)] [backface-visibility:hidden] `}>
                {isFlipped ? content : content}
            </div>
            </div>
        </div>
        </>
    );
};


export default Card;
