import React, { useRef, useState } from 'react';

const AnimatedBorderSquare = () => {
  const boxRef = useRef(null);
  const [borderStyle, setBorderStyle] = useState({
    borderX: 0,
    borderY: 0,
    borderColor: 'transparent',
  });

  const handleMouseEnter = (event) => {
    const { left, top, width, height } = boxRef.current.getBoundingClientRect();
    const mouseX = event.clientX - left;
    const mouseY = event.clientY - top;

    const percentX = (mouseX / width) * 100;
    const percentY = (mouseY / height) * 100;

    setBorderStyle({
      borderX: percentX,
      borderY: percentY,
      borderColor: '#00FF00',
    });

    setTimeout(() => {
      setBorderStyle({
        borderX: percentX + 25,
        borderY: percentY + 25,
        borderColor: '#009900',
      });
    }, 250);

    setTimeout(() => {
      setBorderStyle({
        borderX: percentX + 50,
        borderY: percentY + 50,
        borderColor: '#006600',
      });
    }, 500);

    setTimeout(() => {
      setBorderStyle({
        borderX: percentX + 75,
        borderY: percentY + 75,
        borderColor: '#003300',
      });
    }, 750);
  };

  return (
    <div
      ref={boxRef}
      className="relative w-24 h-24 overflow-hidden bg-blue-500"
      id="box"
      onMouseEnter={handleMouseEnter}
    >
      <div
        className="absolute inset-0 border-solid animation"
        style={{
          borderTop: `${borderStyle.borderY}% solid ${borderStyle.borderColor}`,
          borderBottom: `${100 - borderStyle.borderY}% solid ${borderStyle.borderColor}`,
          borderLeft: `${borderStyle.borderX}% solid ${borderStyle.borderColor}`,
          borderRight: `${100 - borderStyle.borderX}% solid ${borderStyle.borderColor}`,
        }}
      ></div>
      <p>def</p>
    </div>
  );
};

export default AnimatedBorderSquare;
