import React, { useState, useEffect } from 'react';

function ProfileGameHistory(){
    const [gameHistory, setGameHistory] = useState([]);
    const [gameOutcome, setGameOutcome] = useState('');
    const isLoggedInObject = localStorage.getItem('token');
    const isLoggedIn = isLoggedInObject ? JSON.parse(isLoggedInObject).value : null;
    useEffect(() => {
        const handleGetGameHistory = async () => {
            try {
                const response = await fetch('http://localhost/api/getUserGames', {
                    method: 'GET',
                    headers: {
                        'Content-Type': 'application/json',
                        'Authorization': `Bearer ${isLoggedIn}`,
                    },
                });
                if (response.ok) {
                    const data = await response.json();
                    setGameHistory(data.games);
                    console.log(data.games);
                } else {
                    const errorData = await response.json();
                    console.error('Error fetching user info:', errorData);
                }
            } catch (error) {
                console.error('Error fetching user info:', error);
            }
        };
        handleGetGameHistory();
    }, [isLoggedIn]);

    const formatTime = (timeInSeconds) => {
        const minutes = Math.floor(timeInSeconds / 60);
        const seconds = timeInSeconds % 60;
        return `${minutes}:${seconds.toLocaleString('en-US', { minimumIntegerDigits: 2 })}`;
      };

    function getGameOutcome(outcome) {
        switch(outcome){
          case 0:
            setGameOutcome('loss');
            break;
          case 1:
            setGameOutcome('win');
            break;
          default:
            break;
        }
    }
    return(
        <>
        {gameHistory.map((info) => ( 
            <div className='w-full flex flex-col'>
                <h1 className='uppercase text-xl tracking-widest font-light text-center sm:text-start sm:ml-6 mt-4'>Game #2</h1>
                <div className='w-[95%] flex flex-col sm:flex-row border border-neutral-300 rounded-lg m-2 sm:ml-4'>
                    <div className='w-full items-center sm:items-start sm:w-[30%] flex justify-center flex-col mt-2'>
                        <h1 className='uppercase text-2xl tracking-widest'>{info.theme.theme}</h1>
                        <p className='font-light text-lg uppercase text-[rgb(175,169,158)] sm:hidden mb-4 mt-2'>Difficulty:</p>
                        <p className='font-light text-lg uppercase text-[rgb(175,169,158)] hidden sm:flex mb-4 mt-2'>{info.difficulty}d</p>
                    </div>
                    <div className='w-full items-center sm:items-start sm:w-[20%] flex justify-center flex-col'>
                        <h1 className='uppercase text-2xl tracking-widest'>Turns</h1>
                        <p className='font-light text-lg uppercase text-[rgb(175,169,158)] mb-4 mt-2'>{info.turn_count}</p>
                    </div>
                    <div className='w-full items-center sm:items-start sm:w-[20%] flex justify-center flex-col'>
                        <h1 className='uppercase text-2xl tracking-widest'>Time</h1>
                        <p className='font-light text-lg uppercase text-[rgb(175,169,158)] mb-4 mt-2'>{formattedTime}</p>
                    </div>
                    <div className='w-full items-center sm:items-start sm:w-[30%] flex justify-center flex-col'>
                        <h1 className='uppercase text-2xl tracking-widest'>Reward</h1>
                        <p className='font-light text-lg uppercase text-[rgb(175,169,158)] mb-4 mt-2'>1584 ðŸ’¸</p>
                    </div>
                </div>
            </div>
        ))}
        </>
    );
}

export default ProfileGameHistory;
