import React, { useRef, useState } from 'react';

const AnimatedBorderSquare = () => {
  const boxRef = useRef(null);
  const [borderStyle, setBorderStyle] = useState({
    borderX: 0,
    borderY: 0,
    borderColor: 'transparent',
  });

  const handleMouseEnter = (event) => {
    const { left, top, width, height } = boxRef.current.getBoundingClientRect();
    const mouseX = event.clientX - left;
    const mouseY = event.clientY - top;
    console.log(mouseX);
    console.log(mouseY);

    const percentX = (mouseX / width) * 100;
    const percentY = (mouseY / height) * 100;

    setBorderStyle({
      borderX: percentX,
      borderY: percentY,
      borderColor: '#00FF00',
    });
  }
    return(
    <div className="relative w-24 h-24 overflow-hidden bg-blue-500 group">
      <style>
        {`
          @keyframes borderAnimation {
            0% {
              transform: scaleX(0) scaleY(0);
              border-color: transparent;
            }
            25% {
              transform: scaleX(1) scaleY(0);
              border-color: #00FF00; /* Light Green */
            }
            50% {
              transform: scaleX(1) scaleY(1);
              border-color: #009900; /* Darker Green */
            }
            75% {
              transform: scaleX(0) scaleY(1);
              border-color: #006600; /* Even Darker Green */
            }
            100% {
              transform: scaleX(0) scaleY(0);
              border-color: transparent;
            }
          }

          .group:hover .animation {
            animation: borderAnimation 2s infinite;
          }
        `}
      </style>
      <div className="absolute inset-0 border-t-2 border-l-2 border-solid border-transparent animation"></div>
      <div className="absolute inset-0 border-b-2 border-r-2 border-solid border-transparent animation"></div>
      <p>def</p>
    </div>
  );
};

export default AnimatedBorderSquare;
