import React, { useState, useEffect } from 'react';
import Header from './Header';
import ProfileChangePassword from './ProfileChangePassword';

function ProfileInformation() {
    const isLoggedInObject = localStorage.getItem('token');
    const isLoggedIn = isLoggedInObject ? JSON.parse(isLoggedInObject).value : null;

    const [userData, setUserData] = useState({});
    const [userCurrentName, setCurrentName] = useState('');

    const handleChange = (e) => {
        const { name, value, type } = e.target;

        if (type === 'file') {
            const file = e.target.files[0];
            setSelectedFile(file);
        } else {
            setUserData((prevData) => ({
                ...prevData,
                [name]: value,
            }));
        }
    };

    useEffect(() => {
        const fetchUserInfo = async () => {
            try {
                const response = await fetch('http://localhost/api/getUserData', {
                    method: 'GET',
                    headers: {
                        'Content-Type': 'application/json',
                        'Authorization': `Bearer ${isLoggedIn}`,
                    },
                });

                if (response.ok) {
                    const data = await response.json();
                    const userData = data && data.user ? data.user : null;
                    setUserData(userData);
                    setCurrentName(userData.username);
                } else {
                    const errorData = await response.json();
                    console.error('Error fetching user info:', errorData);
                }
            } catch (error) {
                console.error('Error fetching user info:', error);
            }
        };

        fetchUserInfo();
    }, [isLoggedIn]);

    const [isPasswordModalOpen, setIsPasswordModalOpen] = useState(false);

    const togglePasswordModal = () => {
        setIsPasswordModalOpen(!isPasswordModalOpen);
    };

    const closePasswordModal = () => {
        setIsPasswordModalOpen(false);
    };

    const [errors, setErrors] = useState({});
    const [success, setSuccess] = useState('');
    const [fadeTimeout, setFadeTimeout] = useState(null);

    const [selectedFile, setSelectedFile] = useState(undefined);

    const handleSubmitForm = async (e) => {
        e.preventDefault();

        const formDataToSend = new FormData();
        formDataToSend.append('profileUsername', userData.username ? userData.username.trim() : '');
        formDataToSend.append('profileEmail', userData.email ? userData.email.trim() : '');
        formDataToSend.append('profilePicture', selectedFile);

        const validationErrors = {};
        const usernameToClear = userData.username;
        const emailToClear = userData.email;
        const pictureToClear = selectedFile;

        if (!emailToClear) {
            validationErrors.profileEmail = 'E-mail is required';
        }

        if (!usernameToClear) {
            validationErrors.profileUsername = 'Username is required';
        } else if (usernameToClear.match(/^[^a-zA-Z0-9]+$/)) {
            validationErrors.profileUsername = 'Username cannot contain symbols';
        } else if (usernameToClear.trim().length < 3) {
            validationErrors.profileUsername = 'Minimum length is 3 symbols';
        } else if (usernameToClear.trim().length > 15) {
            validationErrors.profileUsername = 'Maximum length is 15 symbols';
        }

        if (!pictureToClear) {
            validationErrors.profileEmail = 'Picture is required';
        }

        setErrors(validationErrors);

        if (Object.keys(validationErrors).length === 0) {
            setErrors({});
            try {
                const response = await fetch('http://localhost/api/updateUserData', {
                    method: 'PUT',
                    headers: {
                        'Authorization': `Bearer ${isLoggedIn}`,
                    },
                    body: formDataToSend,
                });
                const responseData = await response.json();
                if (!response.ok) {
                    setErrors(responseData.errors);
                    setSuccess('');
                } else {
                    setSuccess(responseData.message);
                    setCurrentName(userData.username);
                    const timeout = setTimeout(() => {
                        setSuccess(null);
                    }, 3000);
                    setFadeTimeout(timeout);
                }
            } catch (error) {
                console.error('Error:', error);
            }
        }
    };

    const [opacity, setOpacity] = useState(1);
    useEffect(() => {
        let intervalId;

        if (success) {
            intervalId = setInterval(() => {
                setOpacity((prevOpacity) => Math.max(0, prevOpacity - 0.05));
            }, 100);
        }
        if (opacity === 0) {
            setSuccess(null);
        }

        return () => {
            clearInterval(intervalId);
            setOpacity(1);
        };
    }, [success]);

    return (
        <>
            <Header />
            <div className="overflow-y-scroll min-h-screen bg-[rgb(18,18,18,0.9)] flex justify-center w-full text-white flex-col">
                <div className='w-full h-[10vh]'></div>
                <div className='h-full w-[100%] flex flex-col justify-center items-center my-6'>
                    <div className='w-[85%] flex sm:ml-8 text-center items-center justify-center sm:justify-start'>
                        <h1 className='uppercase text-2xl sm:text-4xl tracking-widest'>Welcome, {userCurrentName}!</h1>
                    </div>
                    <div className='rounded-xl bg-[rgb(44,44,44,0.8)] w-[85%] min-h-[90%] flex flex-col m-4'>
                        <div className='w-full flex flex-col sm:flex-row'>
                            <div className='sm:w-1/2 w-full h-full flex flex-col items-center justify-center m-2 ml-0'>
                                <form className={`w-[85%] h-[600px] transition-all ease-in-out duration-500 mb-4 rounded-xl flex flex-col items-center bg-[rgb(26,26,26,0.9)]`} onSubmit={handleSubmitForm}>
                                    <div className='w-full h-[40%] flex flex-col justify-center items-center m-2'>
                                        <input type='file' id='profilePicture' onChange={handleChange} className='m-2 rounded-[100%] h-[240px] w-[240px] max-[425px]:w-[200px] h-full object-cover shadow-xl shadow-black/40 hover:scale-105 transition-transform duration-150 cursor-pointer' />
                                        {errors.profilePicture && <p className="text-red-500 ml-3 my-2 text-lg">{errors.profilePicture}</p>}
                                    </div>
                                    <div className='w-[90%]'>
                                        <label className='ml-3 text-lg uppercase tracking-widest' htmlFor='profileUsername'>Username:</label>
                                        <input defaultValue={userData.username} onChange={handleChange} className='focus:outline-none bg-transparent hover:scale-105 focus:scale-105 hover:border-neutral-200 focus:border-2 duration-150 border border-neutral-600 text-lg indent-2 p-1 w-full h-10 rounded-sm mb-2' type='text' id='profileUsername' name='profileUsername' />
                                        {errors.profileUsername && <p className="text-red-500 ml-3 my-2 text-lg">{errors.profileUsername}</p>}
                                    </div>
                                    <div className='w-[90%]'>
                                        <label className='ml-3 text-lg uppercase tracking-widest' htmlFor='profileEmail'>E-mail:</label>
                                        <input defaultValue={userData.email} onChange={handleChange} className='focus:outline-none bg-transparent hover:scale-105 focus:scale-105 hover:border-neutral-200 focus:border-2 duration-150 border border-neutral-600 text-lg indent-2 p-1 w-full h-10 rounded-sm mb-2' type='text' id='profileEmail' name='profileEmail' />
                                        {errors.profileEmail && <p className="text-red-500 ml-3 my-2 text-lg">{errors.profileEmail}</p>}
                                    </div>
                                    <input className='mt-4 w-[60%] my-2 h-10 bg-[rgb(33,36,37)] hover:bg-[rgb(39,42,43)] hover:border-neutral-200 focus:border-2 border-neutral-600 border text-white text-2xl rounded-sm duration-500 hover:scale-105 cursor-pointer' type='submit' value='Save' />
                                    <p className={`min-h-[28px] max-[425px]:min-h-[48px] text-center text-green-500 ml-3 my-2 text-lg`} style={{ opacity }}>
                                        {success}
                                    </p>
                                    <div className='w-full text-center justify-center items-center flex'>
                                        <p
                                            onClick={togglePasswordModal}
                                            className='mr-1 max-[426px]:mt-4 w-fit my-2 text-xl hover:bg-white hover:text-black duration-500 transition-bg cursor-pointer p-1 rounded-sm uppercase tracking-wide'
                                        >
                                            Want to change your password?
                                        </p>
                                    </div>
                                </form>
                            </div>
                            <div className='sm:w-1/2 w-full flex justify-center'>
                                <div className='w-full flex flex-col justify-around items-center'>
                                    <div className='w-[85%] sm:min-h-[45%] rounded-xl flex flex-col items-scenter bg-[rgb(26,26,26,0.9)] my-2'>
                                        <h1 className='text-2xl sm:text-3xl tracking-widest font-light uppercase text-center my-2'>YOUR ACHIEVEMENTS:</h1>
                                        <p className='text-xl tracking-widest font-light uppercase text-center text-[rgb(175,169,158)]'>Memoria completion: 0%</p>
                                        <p className='text-xl tracking-widest font-light uppercase text-center text-[rgb(175,169,158)]'>Memoria completion: 0%</p>
                                    </div>
                                    <div className='w-[85%] sm:min-h-[45%] rounded-xl flex flex-col items-scenter bg-[rgb(26,26,26,0.9)] my-2'>
                                        <h1 className='text-2xl sm:text-3xl tracking-widest font-light uppercase text-center my-2'>YOUR COSMETICS:</h1>
                                        <p className='text-xl tracking-widest font-light uppercase text-center text-[rgb(175,169,158)]'>Memoria completion: 0%</p>
                                    </div>
                                </div>
                            </div>
                        </div>
                        <div className='w-full flex justify-center'>
                            <div className='w-[92%] h-full flex flex-col items-center justify-center m-2'>
                                <div className='w-[100%] min-h-[90%] mb-4 rounded-xl flex flex-col items-center bg-[rgb(26,26,26,0.9)]'>
                                    <div className='w-full h-[40%] flex justify-center items-center text-center m-2'>
                                        <h1 className='uppercase text-3xl tracking-widest my-2'>YOUR GAME HISTORY:</h1>
                                    </div>
                                    <div className='w-full flex flex-col'>
                                        <h1 className='uppercase text-xl tracking-widest font-light text-center sm:text-start sm:ml-6 mt-4'>Game #2</h1>
                                        <div className='w-[95%] flex flex-col sm:flex-row border border-neutral-300 rounded-lg m-2 sm:ml-4'>
                                            <div className='w-full items-center sm:items-start sm:w-[30%] flex justify-center flex-col mt-2'>
                                                <h1 className='uppercase text-2xl tracking-widest'>Medieval</h1>
                                                <p className='font-light text-lg uppercase text-[rgb(175,169,158)] sm:hidden mb-4 mt-2'>Insane</p>
                                                <p className='font-light text-lg uppercase text-[rgb(175,169,158)] hidden sm:flex mb-4 mt-2'>Difficulty: Insane</p>
                                            </div>
                                            <div className='w-full items-center sm:items-start sm:w-[20%] flex justify-center flex-col'>
                                                <h1 className='uppercase text-2xl tracking-widest'>Turns</h1>
                                                <p className='font-light text-lg uppercase text-[rgb(175,169,158)] mb-4 mt-2'>38</p>
                                            </div>
                                            <div className='w-full items-center sm:items-start sm:w-[20%] flex justify-center flex-col'>
                                                <h1 className='uppercase text-2xl tracking-widest'>Time</h1>
                                                <p className='font-light text-lg uppercase text-[rgb(175,169,158)] mb-4 mt-2'>1:36</p>
                                            </div>
                                            <div className='w-full items-center sm:items-start sm:w-[30%] flex justify-center flex-col'>
                                                <h1 className='uppercase text-2xl tracking-widest'>Reward</h1>
                                                <p className='font-light text-lg uppercase text-[rgb(175,169,158)] mb-4 mt-2'>1584 💸</p>
                                            </div>
                                        </div>
                                    </div>
                                    <div className='w-full flex flex-col'>
                                        <h1 className='uppercase text-xl tracking-widest font-light text-center sm:text-start sm:ml-6 mt-4'>Game #1</h1>
                                        <div className='w-[95%] flex flex-col sm:flex-row border border-neutral-300 rounded-lg m-2 sm:ml-4'>
                                            <div className='w-full items-center sm:items-start sm:w-[30%] flex justify-center flex-col mt-2'>
                                                <h1 className='uppercase text-2xl tracking-widest'>Futuristic</h1>
                                                <p className='font-light text-lg uppercase text-[rgb(175,169,158)] flex mb-4 mt-2'>Medium</p>
                                                {/* <p className='font-light text-lg uppercase text-[rgb(175,169,158)] max-[426px]:hidden flex mb-4 mt-2'>Difficulty: Medium</p> */}
                                            </div>
                                            <div className='w-full items-center sm:items-start sm:w-[20%] flex justify-center flex-col'>
                                                <h1 className='uppercase text-2xl tracking-widest'>Turns</h1>
                                                <p className='font-light text-lg uppercase text-[rgb(175,169,158)] mb-4 mt-2'>24</p>
                                            </div>
                                            <div className='w-full items-center sm:items-start sm:w-[20%] flex justify-center flex-col'>
                                                <h1 className='uppercase text-2xl tracking-widest'>Time</h1>
                                                <p className='font-light text-lg uppercase text-[rgb(175,169,158)] mb-4 mt-2'>1:05</p>
                                            </div>
                                            <div className='w-full items-center sm:items-start sm:w-[30%] flex justify-center flex-col'>
                                                <h1 className='uppercase text-2xl tracking-widest'>Reward</h1>
                                                <p className='font-light text-lg uppercase text-[rgb(175,169,158)] mb-4 mt-2'>880 💸</p>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                    {isPasswordModalOpen && (
                        <ProfileChangePassword onClose={closePasswordModal} />
                    )}
                </div>
            </div>
        </>
    );
}

export default ProfileInformation;
