import React, { useState, useEffect } from 'react';
import Header from './Header';

const DifficultySelector = ({ onSelectDifficulty, theme }) => {
  const difficulties = [3, 4, 5, 6, 7];
  const [userClearedLevels, setUserClearedLevels] = useState([]);
  const [selectedDifficulty, setSelectedDifficulty] = useState(difficulties[0]);


  const handleStartGame = () => {
    onSelectDifficulty(selectedDifficulty);
  };

  // Map function for textual representation of difficultiess
  const getDifficultyName = (difficulty) => {
    switch (difficulty) {
      case 3:
        return 'Beginner';
      case 4:
        return 'Intermediate';
      case 5:
        return 'Advanced';
      case 6:
        return 'Expert';
      case 7:
        return 'Master';
      default:
        return 'Unknown';
    }
  };

  const getDifficultyColor = (difficulty) => {
    switch (difficulty) {
      case 3:
        return '#C564D2';
      case 4:
        return '#9E35B5';
      case 5:
        return '#720E98';
      case 6:
        return '#4D0A76';
      case 7:
        return '#320652';
      default:
        return 'Unknown';
    }
  };

  return (
    <>
      <Header />
      <div className="overflow-y-scroll min-h-screen bg-[rgb(18,18,18,0.9)] flex items-center justify-center w-full text-white">
        <div className='h-full w-[95%] flex flex-col justify-center items-center'>
          <label className='uppercase font-light tracking-widest text-2xl my-4'>Select Difficulty: {theme}</label>

          {difficulties.map((difficulty) => (
            <div
              key={difficulty}
              className={`w-[300px] transition-all transform duration-500 hover:scale-105 ${selectedDifficulty === difficulty ? 'scale-105' : ''} relative m-2 border border-neutral-200 cursor-pointer`}
              style={{ backgroundColor: selectedDifficulty === difficulty ? getDifficultyColor(difficulty) : '' }}
              onClick={() => setSelectedDifficulty(difficulty)}
            >
              <div className={`w-full text-2xl hover:opacity-80 ${selectedDifficulty === difficulty ? 'opacity-100' : 'opacity-60'}`}>
                <div>
                  <h1 className='text-center uppercase font-light tracking-widest'>
                    {getDifficultyName(difficulty)}
                  </h1>
                </div>
              </div>
            </div>
          ))}
          <button onClick={handleStartGame}>Start Game</button>
        </div>
      </div>
    </>
  );
};

export default DifficultySelector;
