import React, { useState, useEffect } from 'react';

function ProfileBackgroundSelection() {
    const [animatedBackgrounds, setAnimatedBackgrounds] = useState([]);
    const isLoggedInObject = localStorage.getItem('token');
    const isLoggedIn = isLoggedInObject ? JSON.parse(isLoggedInObject).value : null;

    useEffect(() => {
        const getAnimatedBackgrounds = async () => {
            try {
                const response = await fetch('http://localhost/api/getAnimatedBackgrounds', {
                    method: 'GET',
                    headers: {
                        'Authorization': `Bearer ${isLoggedIn}`
                    },
                });
                const responseData = await response.json();
                if (!response.ok) {
                    console.error('Error:', responseData);
                } else {
                    setAnimatedBackgrounds(responseData.backgrounds);
                }
            } catch (error) {
                console.error('Error:', error);
            }
        };

        getAnimatedBackgrounds();
    }, [isLoggedIn]);

    return (
        <>
            <div className='w-[85%] sm:min-h-[45%] rounded-xl flex flex-col bg-[rgb(26,26,26,0.9)] my-2 relative overflow-hidden'>
                <h1 className='text-2xl sm:text-3xl tracking-widest font-light uppercase text-center my-2'>YOUR COSMETICS:</h1>
                <p className='text-xl tracking-widest font-light uppercase text-center text-[rgb(175,169,158)]'>Memoria completion: 0%</p>
                <div className='flex gap-16 overflow-x-scroll snap-x-mandatory scrollbar-thin scrollbar-thumb-gray-500 scrollbar-track-gray-800'>
                    {animatedBackgrounds.map((backgrounds, index) => (
                        <div className='group m-2 mb-4 h-[150px] w-[150px] cursor-pointer [perspective:1000px]' key={index}>
                            <div className='relative h-full w-[150px] rounded-xl shadow-lg transition-all duration-500 [transform-style:preserve-3d] group-hover:[transform:rotateY(180deg)]'>
                                <div className='absolute inset-0'>
                                    <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={1} stroke="currentColor" className="w-16 h-16 z-[2] absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2">
                                        <path strokeLinecap="round" strokeLinejoin="round" d="M16.5 10.5V6.75a4.5 4.5 0 1 0-9 0v3.75m-.75 11.25h10.5a2.25 2.25 0 0 0 2.25-2.25v-6.75a2.25 2.25 0 0 0-2.25-2.25H6.75a2.25 2.25 0 0 0-2.25 2.25v6.75a2.25 2.25 0 0 0 2.25 2.25Z" />
                                    </svg>
                                    <img className='h-full w-full rounded-xl object-cover shadow-xl opacity-75 shadow-black/40' src={`http://localhost/${backgrounds?.image_path}`} alt="" />
                                </div>
                                {/* <div className='absolute inset-0 h-full w-full rounded-xl bg-black/40 text-center text-slate-200 [transform:rotateY(180deg)] [backface-visibility:hidden]'>
                                    <div className='flex w-full min-h-full flex-col items-center justify-center'>
                                        <h1 className='tracking-widest font-light uppercase'>{backgrounds.name}</h1>
                                        <p className='tracking-widest font-light'>{backgrounds.description}</p>
                                        <p className='tracking-widest font-light'>{backgrounds.price}</p>
                                    </div>
                                </div> */}
                            </div>
                        </div>
                    ))}
                </div>
            </div>
        </>
    );
}

export default ProfileBackgroundSelection;
