import React, { useState, useEffect } from 'react';

const shuffleArray = (array) => {
  const shuffledArray = [...array];
  for (let i = shuffledArray.length - 1; i > 0; i--) {
    const j = Math.floor(Math.random() * (i + 1));
    [shuffledArray[i], shuffledArray[j]] = [shuffledArray[j], shuffledArray[i]];
  }
  return shuffledArray;
};

const generateCards = (difficulty) => {
  const symbols = ['A', 'B', 'C', 'D', 'E', 'F', 'G', 'H', 'I', 'J', 'K', 'L'];
  const cardCount = difficulty * difficulty;
  const pairsCount = cardCount / 3; // Assuming you want 3 matching pairs for each difficulty
  const jokerCount = cardCount % 3;

  // Create an array with pairs of symbols
  let cards = symbols.slice(0, pairsCount).flatMap((symbol) => Array(3).fill(symbol));

  // Add joker cards
  cards = cards.concat(Array(jokerCount).fill('Joker'));

  // Double the cards to create pairs

  // Shuffle the cards
  return shuffleArray(cards);
};

const Card = ({ content, isFlipped, isMatched, onClick }) => {
  return (
    // <div className='group rounded-xl h-48 w-48 [perspective:1000px]'>
    //     <div className="relative h-full w-full rounded-xl shadow-xl transition-all duration-500 [transform-style:preserve-3d] group-hover:[transform:rotateY(180deg)]">
    //         <div className='absolute inset-0'>
    //             {/* <div className='h-full w-full rounded-xl object-cover shadow-xl shadow-black/40' >{content}</div> */}
    //             <img className='w-full h-full rounded-xl object-cover shadow-xl shadow-black/40' src="https://i.loli.net/2019/10/18/buDT4YS6zUMfHst.jpg" />
    //         </div>
    //         <div className="absolute inset-0 w-full h-full rounded-xl object-cover text-center text-slate-200 [transform:rotateY(180deg)] [backface-visibility:hidden]">
    //             <img className='w-screen h-full rounded-xl' src="https://i.loli.net/2019/11/16/FLnzi5Kq4tkRZSm.jpg" />
    //         </div>
    //     </div>
    // </div>
    // <div

    //   className={`group rounded-xl h-24 w-24 m-4 [perspective:1000px]`}
    //   onClick={() => !isMatched && onClick()} // Make unclickable if matched
    //   style={{
    //     opacity: isMatched ? 0.5 : 1, // Decrease opacity if matched
    //   }}
    // >
    // <div className={`relative bg-red-500 h-full w-full items-center justify-center flex rounded-xl shadow-xl transition-all text-slate-200 duration-500 [transform-style:preserve-3d] ${isFlipped && !isMatched ? '[transform:rotateY(180deg)]' : (isMatched ? 'bg-green-500' : '')}`}>
    //       {isFlipped && !isMatched ? '' : content}
    //       <div className={`absolute bg-red-500 inset-0 w-full h-full rounded-xl items-center justify-center flex object-cover text-center text-slate-200 [transform-style:preserve-3d] [backface-visibility:hidden] ${isFlipped && !isMatched ? '[transform:rotateY(180deg)]' : (isMatched ? 'bg-green-500' : '')}`}>
    //       {isFlipped && content}
    //     </div>
    //   </div>
    // </div>

    <div
      className={`group rounded-xl h-24 w-24 m-4 [perspective:1000px]`}
      onClick={() => !isMatched && onClick()} // Make unclickable if matched
      style={{
        opacity: isMatched ? 0.5 : 1, // Decrease opacity if matched
      }}
    >
      <div className={`relative bg-red-500 h-full w-full rounded-xl items-center justify-center flex shadow-xl transition-all text-slate-200 duration-500 transition [transform-style:preserve-3d] ${isFlipped && !isMatched ? '[transform:rotateY(360deg)]' : (isMatched ? 'bg-green-500' : '')}`}>
        {!isFlipped && !isMatched ? '' : content}
      </div>
    </div>

  );
};

const MemoryGame = ({ difficulty }) => {
  const [cards, setCards] = useState(generateCards(difficulty));
  const [flippedIndices, setFlippedIndices] = useState([]);
  const [matchedCards, setMatchedCards] = useState([]);
  console.log(flippedIndices);
  console.log(matchedCards);
  useEffect(() => {
    // Shuffle the cards when the component mounts or when the game restarts
    setCards(generateCards(difficulty));
    setFlippedIndices([]);
    setMatchedCards([]);
  }, [difficulty]);

  const handleCardClick = (index) => {
    // Check if the clicked card is already flipped
    if (
      flippedIndices.includes(index) ||
      flippedIndices.length === 3 ||
      matchedCards.some((matched) => matched.indexes.includes(index))
    ) {
      return;
    }

    // Flip the clicked card
    setFlippedIndices([...flippedIndices, index]);

    if (flippedIndices.length === 2) {
      // Check for a match
      const card1 = cards[flippedIndices[0]];
      const card2 = cards[flippedIndices[1]];
      const card3 = cards[index];

      if (card1 === card2 && card2 === card3) {
        const matchedContent = [card1, card2, card3];
        const matchedIndexes = [...flippedIndices, index];
        setMatchedCards((prevMatchedCards) => [
          ...prevMatchedCards,
          { content: matchedContent, indexes: matchedIndexes },
        ]);
        console.log('cards matched');
      }else{
        console.log('cards mismatched');
      }

      // Reset flippedIndices after checking for a match
      setTimeout(() => {
        if(card1 == card2 && card2 === card3){
          setFlippedIndices([]);
        }else{
          setFlippedIndices([]);
        }
      }, 1000);
    }
  };


  return (
  <div className='flex flex-wrap'>
    {cards.map((symbol, index) => (
        <Card
          key={index}
          content={symbol}
          isFlipped={flippedIndices.includes(index)}
          isMatched={matchedCards.some((matched) => matched.indexes.includes(index))}
          onClick={() => handleCardClick(index)}
        />
      ))}
  </div>
);
};

export default MemoryGame;
