import React, { useEffect, useRef } from 'react';
import * as THREE from 'three';
import BIRDS from 'vanta/dist/vanta.birds.min'; // Import the Vanta.js effect you want to use
import NET from 'vanta/dist/vanta.net.min'; // Import the Vanta.js effect you want to use
import TRUNK from 'vanta/dist/vanta.trunk.min'; // Import the Vanta.js effect you want to use
import GLOBE from 'vanta/dist/vanta.globe.min'; // Import the Vanta.js effect you want to use

const VantaBackground = ({ effect }) => {
  const vantaRef = useRef(null);
  let vantaEffect = null;

  useEffect(() => {
    switch (effect) {
        case 'birds':
            vantaEffect = BIRDS({
            el: vantaRef.current,
            THREE: THREE
            });
            break;
        case 'net':
            vantaEffect = NET({
                el:vantaRef.current,
                THREE: THREE
            });
            break;
        case 'trunk':
            vantaEffect = TRUNK({
                el: vantaRef.current,
                THREE: THREE
            });
            break;
        case 'globe':
            vantaEffect = GLOBE({
                el: vantaRef.current,
                THREE: THREE
            });
            break;
      default:
        console.error('Invalid Vanta.js effect specified');
    }

    return () => {
      if (vantaEffect) vantaEffect.destroy();
    };
  }, [effect]);

  return <div ref={vantaRef} style={{ width: '100%', height: '100%' }} />;
};

export default VantaBackground;
