import React, { useState, useEffect } from 'react';
import VantaBackground from './VantaBackground';
function PurchaseBackground({ onClose, backgroundName, backgroundPrice, backgroundDescription }) {
    const isLoggedInObject = localStorage.getItem('token');
    const isLoggedIn = isLoggedInObject ? JSON.parse(isLoggedInObject).value : null;

    useEffect(() => {
        const fetchUserInfo = async () => {
            try {
                const response = await fetch('http://localhost/api/purchase', {
                    method: 'GET',
                    headers: {
                        'Content-Type': 'application/json',
                        'Authorization': `Bearer ${isLoggedIn}`,
                    },
                });

                if (response.ok) {
                    const data = await response.json();
                    const userData = data && data.user ? data.user : null
                    setUserData(userData);
                    setFormData({
                        profileUsername: userData.username || '',
                        profileEmail: userData.email || '',
                    });
                    onUserDataChange(userData, userData.username);
                } else {
                    const errorData = await response.json();
                    console.error('Error fetching user info:', errorData);
                }
            } catch (error) {
                console.error('Error fetching user info:', error);
            }
        };

        fetchUserInfo();
    }, [isLoggedIn]);

    return(
        <>
            <div className={`w-screen h-screen fixed top-0 left-0 bg-black bg-opacity-40 z-40 transition-opacity duration-300 opacity-100`} onClick={onClose}></div>
            <div className='border fixed rounded-lg z-[45] transition-all transform border-neutral-200 bg-[#121212] w-1/2 max-[425px]:w-[300px] h-[550px] flex text-white text-xl -translate-y-1/2 -translate-x-1/2 top-1/2 left-1/2'>
            <button
                onClick={onClose}
                className='absolute right-2 top-2 z-[5] text-3xl text-white cursor-pointer transform transition-all duration-200 hover:text-gray-300'
            >
                X
            </button>
                <div className='w-full h-full relative flex flex-col justify-center items-center m-2'>
                    <div className='w-full h-full flex flex-col justify-center items-center m-2'>
                        <div className='w-full'><VantaBackground effect={backgroundName} size={`w-full h-[75%] absolute top-10`}/></div>
                        <h1 className='uppercase tracking-widest text-3xl font-light absolute top-0 z-[1]'>{backgroundName} background</h1>
                    </div>
                    <div className='w-full z-[1] my-auto flex flex-col justify-center items-center text-center'>
                        <h1 className='font-light tracking-wide text-2xl my-1'>{backgroundDescription}</h1>
                        <h1 className='font-light tracking-widest text-xl my-1'>{backgroundPrice} ðŸ’¸</h1>
                        <button className='w-fit z-[10] mb-4 text-xl hover:bg-white hover:text-black duration-500 transition-bg cursor-pointer p-1 rounded-sm uppercase tracking-widest'>Purchase</button>
                    </div>
                </div>
            </div>
        </>
    );
}

export default PurchaseBackground;
