import React, { useState } from 'react';
import Header from './Header';
import futuristicImage from '../assets/futuristic.jpeg';
import historyImage from '../assets/historic.jpeg';
import natureImage from '../assets/nature.jpeg';

const GameThemeSelector = ({ onSelectTheme }) => {
  const difficulties = 
  [
    'futuristic',
    'nature',
    'historic',
];
console.log(`url('../assets/futuristic.jpeg')`, futuristicImage);
  const [selectedTheme, setSelectedTheme] = useState(difficulties[0]);

  const handleGameThemeSelector = (event) => {
    setSelectedTheme(parseInt(event.target.value, 10));
  };

  const handlePickTheme = () => {
    onSelectTheme(selectedTheme);
  };

  return (
    <>
      <Header />
      <div className="overflow-y-scroll min-h-screen bg-[rgb(18,18,18,0.9)] flex items-center justify-center w-full text-white">
        <div className='h-full w-[95%] flex flex-col justify-center items-center'>
            <label className='uppercase tracking-widest'>Pick a theme:</label>
            <div className='flex flex-wrap justify-center items-center'>
                <div className='w-[315px] relative h-[200px] hover:text-2xl border  border-neutral-200 m-4 rounded-sm text-center justify-center items-center flex cursor-pointer hover:scale-105 transition-all transform duration-500 object-cover bg-cover'>
                    <div className='w-full h-full bg-cover object-cover opacity-60 hover:opacity-100 transition-all transform duration-500' style={{ backgroundImage: `url('../assets/futuristic.jpeg')` }}></div>
                    <div className='absolute'><h1 className='tracking-widest uppercase font-light'>Futuristic</h1></div>
                </div>
                <div className='w-[315px] relative h-[200px] hover:text-2xl border border-neutral-200 m-4 rounded-sm text-center justify-center items-center flex cursor-pointer hover:scale-105 transition-all transform duration-500 object-cover bg-cover'>
                    <div className='w-full h-full bg-cover object-fill opacity-60 hover:opacity-100 transition-all transform duration-500' style={{ backgroundImage: `url(${historyImage})` }}></div>
                    <div className='absolute'><h1 className='tracking-widest uppercase font-light'>Historic</h1></div>
                </div>
                <div className='w-[315px] relative h-[200px] bg-bottom hover:text-2xl border border-neutral-200 m-4 rounded-sm text-center justify-center items-center flex cursor-pointer hover:scale-105 transition-all transform duration-500 object-cover bg-cover'>
                    <div className='w-full h-full bg-cover object-fill opacity-60 hover:opacity-100 transition-all transform duration-500' style={{ backgroundImage: `url(${natureImage})` }}></div>
                    <div className='absolute'><h1 className='tracking-widest uppercase font-light'>Nature</h1></div>
                </div>
            </div>
        </div>
      </div>
    </>
  );
};

export default GameThemeSelector;
