import React, { useState, useEffect } from 'react';

function ProfileAchievements() {
    const [userAchievements, setUserAchievements] = useState([]);
    const isLoggedInObject = localStorage.getItem('token');
    const isLoggedIn = isLoggedInObject ? JSON.parse(isLoggedInObject).value : null;

    useEffect(() => {
        const fetchUserAchievements = async () => {
            try {
                const response = await fetch('http://localhost/api/getUserAchievements', {
                    method: 'GET',
                    headers: {
                        'Authorization': `Bearer ${isLoggedIn}`
                    },
                });
                const responseData = await response.json();
                if (!response.ok) {
                    console.error('Error:', responseData);
                } else {
                    setUserAchievements(responseData.achievements);
                    console.log(responseData);
                    console.log('game and achievements logged');
                }
            } catch (error) {
                console.error('Error:', error);
            }
        };

        fetchUserAchievements();
    }, [isLoggedIn]);

    return (
        <>
            <div className='w-[85%] sm:min-h-[45%] rounded-xl flex flex-col items-scenter bg-[rgb(26,26,26,0.9)] my-2'>
                <h1 className='text-2xl sm:text-3xl tracking-widest font-light uppercase text-center my-2'>YOUR ACHIEVEMENTS:</h1>
                <p className='text-xl tracking-widest font-light uppercase text-center text-[rgb(175,169,158)]'>Memoria completion: 0%</p>
                <div className='w-full flex gap-4 overflow-x-scroll'>
                    {userAchievements.map((achievement) => (
                        <div className='group h-24 w-24 [perspective:1000px]'>
                            <div className='relative h-full w-full rounded-xl shadow-lg transition-all duration-500 [transform-style:preserve-3d] group-hover:[transform:rotateY(180deg)]'>
                                <div className='absolute inset-0'>
                                    <img className='h-full w-full rounded-xl object-cover shadow-xl shadow-black/40' src={`http://localhost/${achievement?.image_path}`} alt="" />
                                </div>
                                <div className='absolute inset-0 h-full w-full rounded-xl bg-black px-12 text-center text-slate-200'></div>
                            </div>
                        </div>
                        // <div key={achievement.id} className="relative">
                        //     <img
                        //         className='m-2 h-[160px] w-[180px] max-[425px]:w-[160px] object-cover shadow-xl transition-transform duration-150 cursor-pointer'
                        //         src={`http://localhost/${achievement?.image_path}`}
                        //         alt='Achievement picture'
                        //     />
                        //     <div className='flex flex-col'>{achievement.achievement}{achievement.description}{new Date(achievement.pivot.created_at).toLocaleDateString()}</div>
                        // </div>
                    ))}
                </div>

            </div>
        </>
    );
}

export default ProfileAchievements;
