import React, { useRef } from 'react';

const AnimatedBorderSquare = () => {
  const boxRef = useRef(null);


  const handleMouseEnter = (event) => {
    const { left, top, width, height } = boxRef.current.getBoundingClientRect();
    const mouseX = event.clientX - left;
    const mouseY = event.clientY - top;

    const percentX = (mouseX / width) * 100;
    const percentY = (mouseY / height) * 100;

    const animation = `borderAnimation 2s infinite`;
    const style = document.styleSheets[0];
    style.insertRule(`#box:hover .animation { animation: ${animation}; }`, style.cssRules.length);

    style.insertRule(`
      @keyframes borderAnimation {
        0%, 100% {
          border-color: transparent;
          border-width: 0;
        }
        ${percentX}%, ${percentY}%, 25% {
          border-color: #00FF00; /* Light Green */
          border-width: 2px;
        }
        ${percentX + 25}%, ${percentY + 25}%, 50% {
          border-color: #009900; /* Darker Green */
          border-width: 2px;
        }
        ${percentX + 50}%, ${percentY + 50}%, 75% {
          border-color: #006600; /* Even Darker Green */
          border-width: 2px;
        }
        ${percentX + 75}%, ${percentY + 75}%, 100% {
          border-color: #003300; /* Darkest Green */
          border-width: 2px;
        }
      }
    `, style.cssRules.length);
  };

  return (
    <div
      ref={boxRef}
      className="relative w-24 h-24 overflow-hidden bg-blue-500"
      id="box"
      onMouseEnter={handleMouseEnter}
    >
      <div className="absolute inset-0 border-solid border-transparent animation"></div>
      <div className="absolute inset-0 border-solid border-transparent animation"></div>
      <p>def</p>
    </div>
  );
};

export default AnimatedBorderSquare;
