import React, { useState } from 'react';
import Header from './Header';
import futuristicImage from '../assets/futuristic.jpeg';
import historyImage from '../assets/historic.jpeg';
import natureImage from '../assets/nature.jpeg';

const GameThemeSelector = ({ onSelectTheme }) => {
  const difficulties = 
  [
    'futuristic',
    'nature',
    'history',
];
  const [selectedTheme, setSelectedTheme] = useState(difficulties[0]);

  const handleGameThemeSelector = (event) => {
    setSelectedTheme(parseInt(event.target.value, 10));
  };

  const handlePickTheme = () => {
    onSelectTheme(selectedTheme);
  };

  return (
    <>
      <Header />
      <div className="overflow-y-scroll min-h-screen bg-[rgb(18,18,18,0.9)] flex items-center justify-center w-full text-white">
        <div className='h-full w-[95%] flex flex-col justify-center items-center'>
          <label>Select a theme:</label>
          <div className='flex flex-wrap justify-center items-center'>
            <div
              className='w-[315px] h-[150px] border border-neutral-200 m-4 rounded-sm bg-red-500 text-center justify-center items-center flex cursor-pointer hover:scale-105 transition-all transform duration-500 object-cover bg-cover'
              style={{ backgroundImage: `url(${futuristicImage})` }}
            >
              <h1 className='tracking-widest uppercase font-light text-2xl'>Futuristic</h1>
            </div>
            <div
              className='w-[315px] h-[150px] border border-neutral-200 m-4 rounded-sm bg-red-500 text-center justify-center items-center flex cursor-pointer hover:scale-105 transition-all transform duration-500 object-fit bg-cover'
              style={{ backgroundImage: `url(${historyImage})` }}
            >
              <h1 className='tracking-widest uppercase font-light text-2xl'>Historic</h1>
            </div>
            <div
              className='w-[315px] h-[150px] border border-neutral-200 m-4 rounded-sm bg-red-500 text-center justify-center items-center flex cursor-pointer hover:scale-105 transition-all transform duration-500 object-fit bg-cover'
              style={{ backgroundImage: `url(${natureImage})` }}
            >   
                <div className='w-full h-full' style={{ backgroundImage: `url(${natureImage})`}}></div>
                <div>
                    <h1 className='tracking-widest uppercase font-light text-2xl'>Nature</h1>
                </div>

            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export default GameThemeSelector;
