// Card.js

import React from 'react';

const Card = ({ content }) => {
  return (
    // <div className="w-48 h-64 relative">
    //   <div className="absolute w-full h-full bg-white border border-gray-300 rounded-lg flex justify-center items-center text-2xl font-bold rotate-180">
    //     Front
    //   </div>
    //   <div className="absolute w-full h-full bg-blue-500 text-white rounded-lg flex justify-center items-center transform rotate-y-180 opacity-0 hover:opacity-100 transition-opacity">
    //     {content}
    //   </div>
    // </div>
    <div className='group bg-red-500 rounded-xl h-48 w-48 [perspective:1000px]'>
        <div class="relative h-full w-full rounded-xl shadow-xl transition-all duration-500 [transform-style:preserve-3d] group-hover:[transform:rotateY(180deg)]">
            <div className='absolute inset-0'>
                <div className='h-full w-full rounded-xl object-cover shadow-xl shadow-black/40' >{content}</div>
            </div>
            <div class="absolute inset-0 h-full w-full rounded-xl bg-black/80 px-12 text-center text-slate-200 [transform:rotateY(180deg)] [backface-visibility:hidden]">
                <div class="flex min-h-full flex-col items-center justify-center">
                <h1 class="text-3xl font-bold">Jane Doe</h1>
                <p class="text-lg">Photographer & Art</p>
                <p class="text-base">Lorem ipsum dolor sit amet consectetur adipisicing.</p>
                <button class="mt-2 rounded-md bg-neutral-800 py-1 px-2 text-sm hover:bg-neutral-900">Read More</button>
                </div>
            </div>
        </div>
    </div>
  );
};

export default Card;
