import React, { useState, useEffect } from 'react';
import PasswordToggle from './PasswordToggle';

function ProfileChangePassword({ onClose }) {
    const [isPasswordModalOpen, setIsPasswordModalOpen] = useState(false);


    const handleSubmitForm = async (e) => {
        e.preventDefault();

        const formData = {
            password: e.target.password ? e.target.password.value.trim() : '',
            confirmPassword: e.target.confirmPassword ? e.target.confirmPassword.value.trim() : '',
        };

    };

    return(
        <>
        <div onClick={onClose} className={`w-screen h-screen fixed top-0 left-0 bg-black bg-opacity-40 z-40 transition-opacity duration-300 opacity-100 ${isPasswordModalOpen ? "opacity-100" : "opacity-0 pointer-events-none"}`}></div>
        <div className={`border-[3px] text-white border-neutral-300 bg-[#181a1b] shadow-lg sm:ml-0 md:ml-0 lg:ml-0 lg:ml-0 min-h-[300px] xl:w-[500px] lg:w-[500px] md:w-[500px] sm:w-[500px] max-[425px]:w-[400px] max-[375px]:w-[350px] max-[325px]:w-[310px]  w-[400px] font-light rounded-md shadow-lg mb-4 max-[769px]:mt-[24px] mt-[48px] absolute -translate-x-1/2 -translate-y-1/2 top-1/2 left-1/2`}>
                <button
                    onClick={onClose}
                    className='absolute top-2 right-2 text-white cursor-pointer hover:text-gray-300'
                >
                    X
                </button>
            <div className='w-full min-h-full flex flex-wrap'>
                    <div className='rounded-full w-20 h-20 bg-[rgb(26,28,29)] absolute -top-8 -right-8 max-[426px]:hidden transform bg-[rgb(236,236,236,1)]'>
                        <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" strokeWidth={0.8} stroke="white" className="w-25 h-28 absolute top-10 left-1/2 transform -translate-x-1/2 -translate-y-1/2 z-50 rounded-full">
                            <path strokeLinecap="round" strokeLinejoin="round" d="M17.982 18.725A7.488 7.488 0 0 0 12 15.75a7.488 7.488 0 0 0-5.982 2.975m11.963 0a9 9 0 1 0-11.963 0m11.963 0A8.966 8.966 0 0 1 12 21a8.966 8.966 0 0 1-5.982-2.275M15 9.75a3 3 0 1 1-6 0 3 3 0 0 1 6 0Z" />
                        </svg>
                    </div>
                    <form className='w-full min-h-full flex flex-col justify-center items-center' onSubmit={(handleSubmitForm)}>
                        <div className='w-[95%]'>
                            <h1 className='text-2xl tracking-widest my-1'>Change your password:</h1>
                            <p className='font-light text-lg uppercase text-[rgb(175,169,158)] mb-4 mt-2'>Enter your new password</p>
                        </div>
                        <div className='w-[90%]'>
                            <label className='ml-3 text-lg uppercase tracking-widest' htmlFor='password'>Password:</label>
                            <PasswordToggle id='password'/>
                        </div>
                        <div className='w-[90%]'>
                        <label className='ml-3 text-lg uppercase tracking-widest' htmlFor='confirmPassword'>Confirm Password:</label>
                            <PasswordToggle id='confirmPassword'/>
                        </div>
                        <input className='mt-4 w-[75%] my-2 h-10 bg-[rgb(33,36,37)] hover:bg-[rgb(39,42,43)] hover:border-neutral-200 focus:border-2 border-neutral-600 border text-white text-2xl rounded-sm duration-500 hover:scale-105 cursor-pointer' type='submit' value='Change' />
                    </form>
            </div>
        </div>
        </>
    );
}

export default ProfileChangePassword;
