// Import the images
import React, { useEffect, useState } from 'react';
import Header from './Header';
import historicImage from '../assets/historic.jpeg';
import futuristicImage from '../assets/futuristic.jpeg';
import natureImage from '../assets/nature.jpeg';

const GameThemeSelector = ({ onSelectTheme }) => {
  const difficulties = ['futuristic', 'historic', 'nature'];
  const [selectedTheme, setSelectedTheme] = useState(difficulties[0]);
  const [isHovered, setIsHovered] = useState(false);

  const handleGameThemeSelector = (event) => {
    setSelectedTheme(event.target.value);
  };

  const handlePickTheme = () => {
    onSelectTheme(selectedTheme);
  };

  const backgroundImageMap = {
    historic: historicImage,
    futuristic: futuristicImage,
    nature: natureImage,
  };

  const [rotation, setRotation] = useState(0);

  const handleMouseLeave = () => {
    setIsHovered(false);
    setRotation((prevRotation) => prevRotation + 360);
  };

  useEffect(() => {
    if (!isHovered) {
      // Save the current rotation when not hovered
      setRotation((prevRotation) => prevRotation + 360); // Assuming 360 is the full rotation
    }
  }, [isHovered]);
  return (
    <>
      <Header />
      <div className="overflow-y-scroll min-h-screen bg-[rgb(18,18,18,0.9)] flex flex-col items-center justify-center w-full text-white">
      <div className='w-full h-[10vh]'></div>
        <div className='h-full w-[95%] flex flex-col justify-center items-center'>
          <label className='uppercase tracking-widest'>Pick a theme:</label>
          <div className='flex flex-wrap justify-center items-center'>
            {/* <div className='animated_card2'>

            </div> */}
            <div className={`overflow-hidden grid h-[500px] w-[300px] relative place-items-center cursor-pointer ${isHovered ? '' : 'paused'}`}
              onMouseEnter={() => setIsHovered(true)}
              onMouseLeave={() => setIsHovered(false)}
            >
                <div className={`before:absolute before:content-[""] before:rotate-[${rotation}] before:top-[-40%] before:left-[25%] before:w-[50%] before:h-[180%] before:bg-[#fff] before:hover:running before:hover:animate-spin after:absolute after:content-[""] after:inset-[3px] after:bg-[#222]`}>
                    {/* Your content goes here if needed */}
                </div>
            </div>

            {difficulties.map((difficulty) => (
              <div
                key={difficulty}
                value={difficulty}
                onClick={handlePickTheme}
                className='w-[315px] max-[375px]:w-[290px] relative h-[200px] hover:text-2xl border  border-neutral-200 m-4 rounded-sm text-center justify-center items-center flex cursor-pointer hover:scale-105 transition-all transform duration-500 object-cover bg-cover'
              >
                <div
                  className='w-full h-full bg-cover object-cover opacity-60 hover:opacity-100 transition-all transform duration-500'
                  style={{ backgroundImage: `url(${backgroundImageMap[difficulty]})` }}
                ></div>
                <div className='absolute'>
                  <h1 className='tracking-widest uppercase font-light'>{difficulty}</h1>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </>
  );
};

export default GameThemeSelector;
