import React, { useState } from 'react';
import VantaBackground from './VantaBackground'; // Your Vanta.js background component

const ProfileBackgroundSelection = () => {
  const [selectedBackground, setSelectedBackground] = useState(null);

  const handleBackgroundChange = (event) => {
    setSelectedBackground(event.target.value);
  };

  return (
    <div className='w-full bg-red-500 mx-auto'>
      <h2>Select Your Animated Background</h2>
      <select value={selectedBackground} onChange={handleBackgroundChange}>
        <option value="birds">Birds</option>
        <option value="dots">dots</option>
        <option value="net">Net</option>
      </select>

      {selectedBackground && (
        <VantaBackground key={selectedBackground} effect={selectedBackground} />
      )}
    </div>
  );
};

export default ProfileBackgroundSelection;
