import React, { useState, useEffect } from 'react';

const shuffleArray = (array) => {
  const shuffledArray = [...array];
  for (let i = shuffledArray.length - 1; i > 0; i--) {
    const j = Math.floor(Math.random() * (i + 1));
    [shuffledArray[i], shuffledArray[j]] = [shuffledArray[j], shuffledArray[i]];
  }
  return shuffledArray;
};

const generateCards = (difficulty) => {
  const symbols = ['A', 'B', 'C', 'D', 'E', 'F', 'G', 'H', 'I', 'J', 'K', 'L'];
  const cardCount = difficulty * difficulty;
  const pairsCount = cardCount / 3; // Assuming you want 3 matching pairs for each difficulty
  const jokerCount = cardCount % 3;

  // Create an array with pairs of symbols
  let cards = symbols.slice(0, pairsCount).flatMap((symbol) => Array(3).fill(symbol));

  // Add joker cards
  cards = cards.concat(Array(jokerCount).fill('Joker'));

  // Double the cards to create pairs

  // Shuffle the cards
  return shuffleArray(cards);
};

const Card = ({ content, isFlipped, onClick }) => {
  return (
    // <div className='group rounded-xl h-48 w-48 [perspective:1000px]'>
    //     <div className="relative h-full w-full rounded-xl shadow-xl transition-all duration-500 [transform-style:preserve-3d] group-hover:[transform:rotateY(180deg)]">
    //         <div className='absolute inset-0'>
    //             {/* <div className='h-full w-full rounded-xl object-cover shadow-xl shadow-black/40' >{content}</div> */}
    //             <img className='w-full h-full rounded-xl object-cover shadow-xl shadow-black/40' src="https://i.loli.net/2019/10/18/buDT4YS6zUMfHst.jpg" />
    //         </div>
    //         <div className="absolute inset-0 w-full h-full rounded-xl object-cover text-center text-slate-200 [transform:rotateY(180deg)] [backface-visibility:hidden]">
    //             <img className='w-screen h-full rounded-xl' src="https://i.loli.net/2019/11/16/FLnzi5Kq4tkRZSm.jpg" />
    //         </div>
    //     </div>
    // </div>
    <div
      className={`group rounded-xl h-24 w-24 m-4 [perspective:1000px]`}
      onClick={onClick}
    >
      <div className={`relative bg-red-500 h-full w-full items-center justify-center flex  rounded-xl shadow-xl transition-all text-slate-200 duration-500 [transform-style:preserve-3d] ${isFlipped ? 'group-hover:[transform:rotateY(180deg)]' : ''}`}>
            {isFlipped && '' }
        <div className={`absolute bg-red-500 inset-0 w-full h-full rounded-xl items-center justify-center flex object-cover text-center text-slate-200 [transform-style:preserve-3d] ${isFlipped ? '[transform:rotateY(180deg)] [backface-visibility:hidden]' : ''}`}>
          {isFlipped && content}
        </div>
      </div>
    </div>
  );
};

const MemoryGame = ({ difficulty }) => {
  const [cards, setCards] = useState([]);
  const [flippedIndexes, setFlippedIndexes] = useState([]);
  const [matchedPairs, setMatchedPairs] = useState([]);

  // Generate an array of unique content pairs for the cards
  const generateContentPairs = () => {
    const totalPairs = (difficulty * difficulty) / 2;
    const contentArray = [];

    for (let i = 0; i < totalPairs; i++) {
      contentArray.push(`Content ${i + 1}`);
      contentArray.push(`Content ${i + 1}`);
    }

    return contentArray.sort(() => Math.random() - 0.5);
  };

  const handleCardClick = (id) => {
    if (flippedIndexes.length < 3 && !flippedIndexes.includes(id)) {
      setCards((prevCards) =>
        prevCards.map((row) =>
          row.map((card) =>
            card.id === id
              ? { ...card, isFlipped: !card.isFlipped }
              : card
          )
        )
      );
      setFlippedIndexes((prevIndexes) => [...prevIndexes, id]);
    }
  };

  useEffect(() => {
    const gridSize = 4; // Set the grid size (minimum 3, maximum 6)
    const initialCards = Array.from({ length: gridSize }, (_, row) =>
      Array.from({ length: gridSize }, (_, col) => ({
        id: `${row}-${col}`,
        content: '',
        isFlipped: false,
      }))
    );

    setCards(initialCards);
  }, []);

  // Initialize the game when the component mounts
  useEffect(() => {
    const initialCards = Array.from({ length: difficulty }, (_, row) =>
      Array.from({ length: difficulty }, (_, col) => ({
        id: `${row}-${col}`,
        content: '',
        isFlipped: false,
      }))
    );

    setCards(initialCards);
  }, [difficulty]);


  return (
    <div className="flex flex-wrap justify-center">
      {cards.map((row, rowIndex) =>
        row.map((card, colIndex) => (
          <Card
            key={card.id}
            content={card.isFlipped ? card.content : ''}
            isFlipped={card.isFlipped}
            onClick={() => handleCardClick(card.id)}
          />
        ))
      )}
    </div>
  );
};

export default MemoryGame;
