import React, { useRef, useState, useEffect } from 'react';

const AnimatedBorderSquare = () => {
  const boxRef = useRef(null);
  const [borderStyle, setBorderStyle] = useState({
    borderX: 0,
    borderY: 0,
    borderColor: 'transparent',
  });

  useEffect(() => {
    const handleMouseEnter = (event) => {
      const { left, top, width, height } = boxRef.current.getBoundingClientRect();
      const mouseX = event.clientX - left;
      const mouseY = event.clientY - top;

      const percentX = (mouseX / width) * 100;
      const percentY = (mouseY / height) * 100;

      setBorderStyle({
        borderX: percentX,
        borderY: percentY,
        borderColor: '#00FF00',
      });
    };

    boxRef.current.addEventListener('mouseenter', handleMouseEnter);

    return () => {
      boxRef.current.removeEventListener('mouseenter', handleMouseEnter);
    };
  }, []);

  useEffect(() => {
    const style = document.createElement('style');
    style.innerHTML = `
      @keyframes borderAnimation {
        0% {
          transform: translateX(${borderStyle.borderX}%) translateY(${borderStyle.borderY}%);
          border-color: transparent;
        }
        25% {
          transform: translateX(${borderStyle.borderX}%) translateY(${borderStyle.borderY}%);
          border-color: #00FF00; /* Light Green */
        }
        50% {
          transform: translateX(${borderStyle.borderX}%) translateY(${borderStyle.borderY}%);
          border-color: #009900; /* Darker Green */
        }
        75% {
          transform: scaleX(0) scaleY(1) translateX(${borderStyle.borderX}%) translateY(${borderStyle.borderY}%);
          border-color: #006600; /* Even Darker Green */
        }
        100% {
          transform: translateX(${borderStyle.borderX}%) translateY(${borderStyle.borderY}%);
          border-color: transparent;
        }
      }

      .group:hover .animation {
        animation: borderAnimation 2s infinite;
      }
    `;

    document.head.appendChild(style);

    return () => {
      document.head.removeChild(style);
    };
  }, [borderStyle]);

  return (
    <div className="relative w-24 h-24 overflow-hidden bg-blue-500 group" id='box' ref={boxRef}>
      <div className="absolute inset-0 border-2 border-solid border-transparent animation"></div>
      <div className="absolute inset-0 border-2 border-solid border-transparent animation"></div>
      <p>def</p>
    </div>
  );
};

export default AnimatedBorderSquare;
