import Authorization from './views/Authorization.jsx';
import Profile from './views/Profile.jsx';
import MemoryGame from './views/MemoryGame';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
function App() {
  return (
    <>
    <Router>
      <div className='w-screen overflow-y-scroll flex bg-[rgb(18,18,18,0.9)]'>
        <Routes>
          <Route exact path="/Authorize" element = {<Authorization />}/>
          <Route exact path="/Profile" element = {<Profile />}/>
          <Route exact path="/memory-game/:difficulty" element = {<MemoryGame/>}/>
        </Routes>
      </div>
    </Router>
    </>
  );
}

const MemoryGameView = ({ match }) => {
  const difficulty = parseInt(match.params.difficulty, 10);

  if (isNaN(difficulty) || difficulty < 3 || difficulty > 6) {
    // Handle invalid difficulty (redirect or show an error)
    return <div>Invalid difficulty</div>;
  }

  return (
    <div>
      <h2>Memory Game</h2>
      <MemoryGame difficulty={difficulty} />
    </div>
  );
};

export default App;
