import React, { useRef, useState, useEffect } from 'react';

const AnimatedBorderSquare = () => {

      const [hoverPosition, setHoverPosition] = useState(null);

      const startHover = (event) => {
        const rect = event.target.getBoundingClientRect();
        const boxTop = rect.top;
        const boxLeft = rect.left;
        const boxBottom = rect.bottom;
        const boxRight = rect.right;

        const x = event.clientX;
        const y = event.clientY;

        // Calculate the closest point on the box borders
        const startX = Math.min(Math.max(x, boxLeft), boxRight);
        const startY = Math.min(Math.max(y, boxTop), boxBottom);

        setHoverPosition({ x: startX - boxLeft, y: startY - boxTop });
      };

      const endHover = () => {
        setHoverPosition(null);
      };

      return (
        <div className="relative w-[200px] h-[200px] bg-[#f0f0f0] flex align-center justify-center [transition: border 0.3s ease-in-out]" onMouseEnter={startHover} onMouseLeave={endHover}>
          Hover me
          {hoverPosition && (
            <div
              className="absolute top-0 left-0 right-0 bottom-0 border-2 border-[#3498db] border-solid [transform-origin: center]  transformY(scale0) transformX(scale0) [transition: transform 0.3s ease-in-out]"
              style={{
                top: hoverPosition.y + 'px',
                left: hoverPosition.x + 'px',
              }}
            />
          )}
        </div>
      );
    };

export default AnimatedBorderSquare;
