import React, { useEffect, useRef } from 'react';
import * as THREE from 'three';
import VANTA from 'vanta';

const VantaBackground = ({ effect }) => {
  const vantaRef = useRef(null);
  const effectInstance = useRef(null);

  useEffect(() => {
    // Initialize Vanta.js background effect based on the selected effect
    switch (effect) {
      case 'waves':
        effectInstance.current = VANTA.WAVES({
          el: vantaRef.current,
          THREE: THREE
          // Additional options for the Waves effect
        });
        break;
      case 'clouds':
        effectInstance.current = VANTA.CLOUDS({
          el: vantaRef.current,
          THREE: THREE
          // Additional options for the Clouds effect
        });
        break;
      case 'particles':
        effectInstance.current = VANTA.PARTICLES({
          el: vantaRef.current,
          THREE: THREE
          // Additional options for the Particles effect
        });
        break;
      default:
        break;
    }

    // Cleanup function
    return () => {
      if (effectInstance.current) {
        effectInstance.current.destroy();
      }
    };
  }, [effect]);

  return <div ref={vantaRef} style={{ width: '100%', height: '100%' }} />;
};

export default VantaBackground;
