// Card.js

import React from 'react';

const Card = ({ content }) => {
  return (
    <div className="w-48 h-64 relative">
      <div className="absolute w-full h-full bg-white border border-gray-300 rounded-lg flex justify-center items-center text-2xl font-bold">
        Front
      </div>
      <div className="absolute w-full h-full bg-blue-500 text-white rounded-lg flex justify-center items-center transform rotate-180 opacity-0 hover:opacity-100 transition-opacity">
        {content}
      </div>
    </div>
  );
};

export default Card;
