import React, { useState, useEffect } from 'react';
import Header from './Header';
function Leaderboard(){

    const [gameThemes, setGameThemes] = useState([]);
    const [selectedDifficulty, setSelectedDifficulty] = useState('');

    useEffect(() => {
        const fetchUserInfo = async () => {
            try {
                const response = await fetch('http://localhost/api/getGameThemes', {
                    method: 'GET',
                    headers: {
                        'Content-Type': 'application/json',
                    },
                });

                if (response.ok) {
                    const data = await response.json();
                    setGameThemes(data);
                } else {
                    const errorData = await response.json();
                    console.error('Error fetching user info:', errorData);
                }
            } catch (error) {
                console.error('Error fetching user info:', error);
            }
        };

        fetchUserInfo();
    }, []);

    const difficulties = [
        { value: '3', label: '3x3' },
        { value: '4', label: '4x4' },
        { value: '5', label: '5x5' },
        { value: '6', label: '6x6' },
        { value: '7', label: '7x7' },
    ];

    return(
        <>
        <Header/>
        <div className="overflow-y-scroll min-h-screen bg-[rgb(18,18,18,0.9)] flex justify-center w-full text-white flex-col">
            <div className='w-full h-[10vh]'></div>
            <div className='h-full w-[100%] flex flex-col justify-center items-center my-6'>
                <div className='rounded-xl bg-[rgb(44,44,44,0.8)] w-[85%] min-h-[90%] flex flex-col m-4'>
                    <select
                        className='bg-[rgb(26,26,26,0.9)] flex font-light text-center w-[6%] h-8 rounded-lg border border-neutral-200 m-2 hover:scale-105 focus:scale-105 transition-all transform duration-500 cursor-pointer'
                        value={selectedDifficulty}
                        onChange={(e) => setSelectedDifficulty(e.target.value)}
                    >
                        {difficulties.map(({ value, label }) => (
                            <option key={value} value={value}>
                                {label}
                            </option>
                        ))}
                    </select>
                </div>
            </div>
        </div>
        </>
    );
}

export default Leaderboard;
