                        <div className='w-full'><VantaBackground effect={backgroundName} size={`w-full h-[75%] absolute top-10`}/></div>
                        <h1 className='uppercase tracking-widest text-3xl font-light absolute top-0 z-[1]'>Preview of <span className="underline">{backgroundName}</span></h1>

                    </div>
                    <div className='w-full flex justify-center mt-auto flex-col items-center'>
