                        <button className='w-fit z-[10] my-4 text-xl hover:bg-white hover:text-black duration-500 transition-bg cursor-pointer p-1 rounded-sm uppercase tracking-widest'>Purchase</button>
                    </div>
                </div>
            </div>
        </>
    );
}

export default PurchaseBackground;
