import React, { useState, useEffect } from 'react';
import Header from './Header';
import ProfileChangePassword from './ProfileChangePassword';
function ProfileInformation(){

    const localToken = JSON.parse(localStorage.getItem('token'));
    console.log(localToken.value);

    const [userData, setUserData] = useState(null);
    useEffect(() => {
        const fetchUserData = async () => {
          try {
            const response = await fetch('http://localhost/api/getUserData', {
              method: 'GET',
              headers: {
                Authorization: `Bearer ${localToken}`,
                'Content-Type': 'application/json',
              },
            });

            if (response.ok) {
              const data = await response.json();
              setUserData(data.user);
            } else {
              console.error('Failed to fetch user data');
            }
          } catch (error) {
            console.error('Error fetching user data', error);
          }
        };

        fetchUserData();
    }, []);
    console.log(userData);

    const [isPasswordModalOpen, setIsPasswordModalOpen] = useState(false);

    const togglePasswordModal = () => {
        setIsPasswordModalOpen(!isPasswordModalOpen);
    };

    const closePasswordModal = () => {
        setIsPasswordModalOpen(false);
    };

    const [errors, setErrors] = useState({});
    return(
        <>
        <Header/>
        <div className="overflow-y-scroll min-h-screen bg-[rgb(18,18,18,0.9)] flex justify-center w-full text-white flex-col">
            <div className='w-full h-[10vh]'></div>
            <div className='h-full w-[100%] flex flex-col justify-center items-center my-6'>
                <div className='w-[85%] flex sm:ml-8 text-center items-center justify-center sm:justify-start'>
                    <h1 className='uppercase text-2xl sm:text-4xl tracking-widest'>Welcome, usernamegoeshere!</h1>   
                </div>
                <div className='rounded-xl bg-[rgb(44,44,44,0.8)] w-[85%] min-h-[90%] flex flex-col m-4'>
                    <div className='w-full flex flex-col sm:flex-row'>
                        <div className='sm:w-1/2 w-full h-full flex flex-col items-center justify-center m-2 ml-0'>
