import React, { useEffect, useState } from 'react';

function ResultScreen({ turns, time, pairs, gamestate }) {
    const [gameStatus, setStatus] = useState('');

    useEffect(() => {
        setCards(generateCards(difficulty, theme));
        setFlippedCards([]);
        setMatchedCards([]);
        setInitialReveal(true);

        const revealTimeout = setTimeout(() => {
          setInitialReveal(false);
        }, difficulty > 4 ? 6000 : 3000);

        return () => clearTimeout(revealTimeout);
      }, [difficulty, theme]);
    const getGameOutcome = () => {
        switch (gamestate) {
            case true:
                setStatus('won');
                break;
            case false:
                setStatus('lost');
                break;
            default:
                break;
        }
    };
    getGameOutcome();

    return (
        <>
            <div className={`w-screen h-screen fixed top-0 left-0 bg-black bg-opacity-40 z-40 transition-opacity duration-300 opacity-100`}></div>
            <div className='border fixed z-[45] border-neutral-200 bg-[rgb(10,10,10)] rounded-xl w-[315px] flex text-white text-xl -translate-y-1/2 -translate-x-1/2 top-1/2 left-1/2'>
                <div className='w-full h-full relative flex flex-col justify-center items-center m-2'>
                    <h1 className='text-2xl tracking-wider uppercase m-2'>You {gameStatus}</h1>
                    <div className='w-full justify-between flex my-1'>
                        <p className='uppercase font-light tracking-wider'>Turns:</p>
                        <p className='font-mono'>{turns}</p>
                    </div>
                    <div className='w-full justify-between flex my-1'>
                        <p className='uppercase font-light tracking-wider'>Time remaining:</p>
                        <p className='font-mono'>{time}</p>
                    </div>
                    <div className='w-full justify-between flex my-1'>
                        <p className='uppercase font-light tracking-wider'>Pairs matched:</p>
                        <p className='font-mono'>{pairs}</p>
                    </div>
                    <div className='w-full justify-between flex my-1'>
                        <p className='uppercase font-light tracking-wider'>Dinero earned:</p>
                        <p className='font-mono'>273</p>
                    </div>
                    <div className='w-full text-center justify-center items-center flex flex-col mt-2'>
                        <p className='mr-1 w-fit my-1 text-xl hover:bg-white hover:text-black duration-500 transition-bg cursor-pointer p-1 rounded-sm uppercase tracking-wide'>Play again</p>
                        <p className='mr-1 w-fit text-xl hover:bg-white hover:text-black duration-500 transition-bg cursor-pointer p-1 rounded-sm uppercase tracking-wide'>Next level</p>
                    </div>
                </div>
            </div>
        </>
    );
}

export default ResultScreen;
