import react, { useState } from 'react';
import PasswordToggle from './PasswordToggle';
function GoogleUserFirstTime({ onClose }) {
    const [errors, setErrors] = useState({});

    const handleSubmitFrom = async (e) => {
        e.preventDefault();

        const validationErrors = {};
        const usernameToClear = e.target.googleUsername;

        if (!usernameToClear) {
            validationErrors.googleUsername = 'Username is required';
        } else if (usernameToClear.match(/^[^a-zA-Z0-9]+$/)){
            validationErrors.registerUsername = 'Username cannot contain symbols';
        } else if(usernameToClear.trim().length < 3){
            validationErrors.registerUsername = 'Minimum length is 3 symbols';
        } else if(usernameToClear.trim().length > 15){
            validationErrors.registerUsername = 'Maximum length is 15 symbols';
        }
    }
    return (
        <>
        <div onClick={onClose} className={`w-screen h-screen fixed top-0 left-0 bg-black bg-opacity-40 z-40 transition-opacity duration-300 opacity-100`}></div>
        <div className={`border-[3px] z-[45] text-white border-neutral-300 bg-[#181a1b] shadow-lg sm:ml-0 md:ml-0 lg:ml-0 lg:ml-0 min-h-[250px] xl:w-[500px] lg:w-[500px] md:w-[500px] sm:w-[500px] max-[425px]:w-[400px] max-[375px]:w-[350px] max-[325px]:w-[310px]  w-[400px] font-light rounded-md shadow-lg mb-4 max-[769px]:mt-[24px] mt-[48px] fixed -translate-x-1/2 -translate-y-1/2 top-1/2 left-1/2`}>
            <div className='w-full min-h-full flex flex-wrap'>
                <form className='w-full min-h-full flex flex-col justify-center items-center'>
                    <div className='w-[95%]'>
                        <h1 className='text-3xl tracking-widest my-1'>Hey! Welcome to Memoria!</h1>
                        <p className='font-light text-lg uppercase text-[rgb(175,169,158)] mb-4 mt-2'>Choose your username to continue</p>
                    </div>
                    <div className='w-[90%]'>
                        <label className='ml-3 text-lg uppercase tracking-widest' htmlFor='googleUsername'>Username:</label>
                        <input className='focus:outline-none bg-transparent hover:scale-105 focus:scale-105 hover:border-neutral-200 focus:border-2 duration-150 border border-neutral-600 text-lg indent-2 p-1 w-full h-10 rounded-sm mb-2' type='text' id='googleUsername' name='googleUsername'/>
                    </div>
                    <input className='mt-4 w-[75%] my-2 h-10 bg-[rgb(33,36,37)] hover:bg-[rgb(39,42,43)] hover:border-neutral-200 focus:border-2 border-neutral-600 border text-white text-2xl rounded-sm duration-500 hover:scale-105 cursor-pointer' type='submit' value='Submit' />
                </form>
            </div>
        </div>
        </>
    );  
}

export default GoogleUserFirstTime;
