import React from 'react';

const GameInfo = ({ currentTime, currentTurnCount, selectedTheme}) => {
  return (
    <>
    <div className='flex justify-around w-1/2 max-[425px]:w-full m-4'>
        <h1>Timer: {currentTime}</h1>
        <h1>Turns: {currentTurnCount}</h1>
        <h1>Theme: {selectedTheme}</h1>
    </div>
    </>
  );
};

export default GameInfo;
