// Card.js

import React, { useState } from 'react';

const Card = ({ id, content, isFlipped, handleCardClick }) => {
    const handleClick = () => {
        if (!isFlipped) {
        handleCardClick(id);
        }
    };

    return (
    <div
        className={`perspective-1000 w-48 h-64 transform-gpu transition-transform cursor-pointer ${
            isFlipped ? 'scale-105' : ''
        }`}
        onClick={handleClick}
    >
        <div className="w-full h-full transform-gpu transition-transform">
            <div className="w-full h-full bg-white border border-gray-300 rounded-lg flex justify-center items-center text-2xl font-bold">
            Front
            </div>
            <div
            className={`w-full h-full bg-blue-500 text-white rounded-lg flex justify-center items-center transform ${
                isFlipped ? 'rotate-y-180' : ''
            }`}
            >
            {content}
            </div>
        </div>
    </div>
  );
};

export default Card;
