import React, { useEffect, useRef } from 'react';
import * as THREE from 'three';
import BIRDS from 'vanta/dist/vanta.birds.min'; // Import the Vanta.js effect you want to use

const VantaBackground = ({ effect }) => {
  const vantaRef = useRef(null);
  let vantaEffect = null;

  useEffect(() => {
    switch (effect) {
      case 'waves':
        vantaEffect = BIRDS({
          el: vantaRef.current,
          THREE: THREE
        });
        break;
      // Add cases for other Vanta.js effects as needed
      default:
        console.error('Invalid Vanta.js effect specified');
    }

    return () => {
      if (vantaEffect) vantaEffect.destroy();
    };
  }, [effect]);

  return <div ref={vantaRef} style={{ width: '100%', height: '100%' }} />;
};

export default VantaBackground;
