import React, { useState, useEffect } from 'react';
import Card from './Card';
const shuffleArray = (array) => {
  const shuffledArray = [...array];
  for (let i = shuffledArray.length - 1; i > 0; i--) {
    const j = Math.floor(Math.random() * (i + 1));
    [shuffledArray[i], shuffledArray[j]] = [shuffledArray[j], shuffledArray[i]];
  }
  return shuffledArray;
};

const generateCards = (difficulty) => {
  const symbols = ['A', 'B', 'C', 'D', 'E', 'F', 'G', 'H', 'I', 'J', 'K', 'L', 'M', 'N', 'O', 'P'];
  const cardCount = difficulty * difficulty;
  const pairsCount = cardCount / 3; // Assuming you want 3 matching pairs for each difficulty
  const jokerCount = cardCount % 3;

  // Create an array with pairs of symbols
  let cards = symbols.slice(0, pairsCount).flatMap((symbol) => Array(3).fill(symbol));

  // Add joker cards
  cards = cards.concat(Array(jokerCount).fill('Joker'));

  // Shuffle the cards
  return shuffleArray(cards);
};

const MemoryGame = ({ difficulty }) => {
  const totalPairs = difficulty * difficulty / 3;
  console.log(difficulty);
  const [cards, setCards] = useState(generateCards(difficulty));
  const [flippedCards, setFlippedCards] = useState([]);
  const [matchedCards, setMatchedCards] = useState([]);
  const [initialReveal, setInitialReveal] = useState(true);
  const [timeLeft, updateTimeLeft] = useState(240);
  const [currentTurnCount, setTurnCount] = useState(0);
  const [gameOver, setGameOver] = useState(false);
  // console.log(flippedCards);
  // console.log(matchedCards);

  useEffect(() => {
    // Shuffle the cards when the component mounts or when the game restarts
    setCards(generateCards(difficulty));
    setFlippedCards([]);
    setMatchedCards([]);
    setInitialReveal(true);

    const revealTimeout = setTimeout(() => {
      setInitialReveal(false);
    }, 2500);

    return () => clearTimeout(revealTimeout);
  }, [difficulty]);

  useEffect(() => {
    if (!initialReveal && timeLeft > 0 && matchedCards.length === totalPairs) {
        console.log('You win!');
        setGameOver(true);
    }
    if(!initialReveal && timeLeft === 0 || timeLeft < 0){
      console.log('You lose!');
      setGameOver(true);
    }
  }, [initialReveal, timeLeft, matchedCards, totalPairs]);

  useEffect(() => {
    if (!initialReveal && timeLeft > 0) {
      if(gameOver != true){
        const timerInterval = setInterval(() => {
          updateTimeLeft((prevTime) => prevTime - 1);
        }, 1000);
        return () => clearInterval(timerInterval);
      }
    }
  }, [initialReveal, timeLeft, matchedCards, totalPairs]);
  const handleCardClick = (index) => {
    if (initialReveal || gameOver) {
      return;
    }
    // Check if the clicked card is already flipped
    if (flippedCards.includes(index) || flippedCards.length === 3 || matchedCards.some((matched) => matched.indexes.includes(index))) {
      return;
    }
    // Flip the clicked card
    setFlippedCards([...flippedCards, index]);

    if (flippedCards.length === 2) {
      // Check for a match
      const card1 = cards[flippedCards[0]];
      const card2 = cards[flippedCards[1]];
      const card3 = cards[index];

      if (card1 === card2 && card2 === card3) {
        const matchedContent = [card1, card2, card3];
        const matchedIndexes = [...flippedCards, index];
        setMatchedCards((prevMatchedCards) => [
          ...prevMatchedCards,
          { content: matchedContent, indexes: matchedIndexes },
        ]);
        console.log('cards matched');
        setFlippedCards([]);
        setTurnCount(currentTurnCount+1);
      } else {
        console.log('cards mismatched');
        setTurnCount(currentTurnCount+1);
      }

      setTimeout(() => {
        setFlippedCards([]);
      }, 1000);

    }
  };
  const formattedTime = `${Math.floor(timeLeft / 60)}:${(timeLeft % 60).toLocaleString('en-US', { minimumIntegerDigits: 2 })}`;

  function getGridScale(difficulty) {
    let baseScale = 0.125
    switch(difficulty){
      case 3:
        baseScale *= 1.4;
        break;
      case 4:
        baseScale *= 1.3;
        break;
      case 5:
        baseScale *= 1.25;
        break;
      default:
        break;
    }
    return Math.min(window.innerWidth * baseScale, 100);
  }

  return (
    <div className="overflow-y-scroll min-h-screen bg-[rgb(18,18,18,0.9)] flex justify-center w-full text-white flex-col">
      <div className='w-full h-[10vh]'></div>
      <div className='w-full h-full flex flex-col justify-center items-center'>
        <div className='flex flex-col w-full  h-full items-center justify-center overflow-x-hidden'>
          <div className='flex justify-around w-1/2 max-[425px]:w-full m-4'>
            <h1>Timer: {formattedTime}</h1>
            <h1>Turns: {currentTurnCount}</h1>
          </div>
            <div className={`flex items-center justify-center grid grid-cols-${difficulty} gap-3 mb-4`}>
            {cards.map((symbol, index) => (
              <Card
                key={index}
                content={symbol}
                isFlipped={initialReveal || flippedCards.includes(index) || matchedCards.some((matched) => matched.indexes.includes(index))}
                isMatched={matchedCards.some((matched) => matched.indexes.includes(index))}
                onClick={() => handleCardClick(index)}
                cardSize={getGridScale(difficulty)}
              />
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default MemoryGame;
