import React, { useState } from 'react';
import DifficultySelector from '../components/DifficultySelector';
import GameThemeSelector from '../components/GameThemeSelector';
import MemoryGame from '../components/MemoryGame';
import Header from '../components/Header';
import BackgroundReceiver from '../components/BackgroundReceiver';

function StartGame(){
    const [selectedTheme, setSelectedTheme] = useState(null);
    const [selectedDifficulty, setSelectedDifficulty] = useState(null);

    const isLoggedInObject = localStorage.getItem('token');
    const isLoggedIn = isLoggedInObject ? JSON.parse(isLoggedInObject).value : null;

    const handleSelectDifficulty = (difficulty) => {
        setSelectedDifficulty(difficulty);
    };

    const handleThemeSelect = (theme) => {
        setSelectedTheme(theme);
    }

    const handlePlayClick = () => {
      setSelectedDifficulty(null);
      setSelectedTheme(null);
    };
    const handleResetTheme = () => {
      setSelectedDifficulty(null);
      setSelectedTheme(null);
  };


    return (
        <>
          <Header onPlayClick={handlePlayClick}/>
          <BackgroundReceiver isLoggedIn={isLoggedIn}/>
          {selectedTheme ? (
            selectedDifficulty ? (
              <MemoryGame difficulty={selectedDifficulty} theme={selectedTheme} />
            ) : (
              <DifficultySelector onSelectDifficulty={handleSelectDifficulty} theme={selectedTheme} handleResetTheme/>
            )
          ) : (
            <GameThemeSelector onSelectTheme={handleThemeSelect} />
          )}
        </>
      );
};

export default StartGame;
