import React, { Component } from 'react';
import * as THREE from 'three';
import BIRDS from 'vanta/dist/vanta.birds.min';
import NET from 'vanta/dist/vanta.net.min';
import TRUNK from 'vanta/dist/vanta.trunk.min';
import GLOBE from 'vanta/dist/vanta.globe.min';

class VantaBackground extends Component {
  constructor(props) {
    super(props);
    this.vantaRef = React.createRef();
    this.vantaEffect = null;
  }

  componentDidMount() {
    this.initVantaEffect();
  }

  componentDidUpdate(prevProps) {
    if (prevProps.effect !== this.props.effect) {
      if (this.vantaEffect) {
        this.vantaEffect.destroy();
      }
      this.initVantaEffect();
    }
  }

  componentWillUnmount() {
    if (this.vantaEffect) {
      this.vantaEffect.destroy();
    }
  }

  initVantaEffect() {
    const { effect } = this.props;
    switch (effect) {
      case 'birds':
        this.vantaEffect = BIRDS({
          el: this.vantaRef.current,
          THREE: THREE
        });
        break;
      case 'net':
        this.vantaEffect = NET({
          el: this.vantaRef.current,
          THREE: THREE
        });
        break;
      case 'trunk':
        this.vantaEffect = TRUNK({
          el: this.vantaRef.current,
          THREE: THREE
        });
        break;
      case 'globe':
        this.vantaEffect = GLOBE({
          el: this.vantaRef.current,
          THREE: THREE
        });
        break;
      default:
        console.error('Invalid Vanta.js effect specified');
    }
  }

  render() {
    return <div ref={this.vantaRef} style={{ width: '100%', height: '100%' }} />;
  }
}

export default VantaBackground;
