import React, { useState } from 'react';
import Header from './Header';

const DifficultySelector = ({ onSelectDifficulty }) => {
  const difficulties = [3, 4, 5, 6, 7];
  const [selectedDifficulty, setSelectedDifficulty] = useState(difficulties[0]);


  const handleStartGame = () => {
    onSelectDifficulty(selectedDifficulty);
  };

  // Map function for textual representation of difficulties
  const getDifficultyName = (difficulty) => {
    switch (difficulty) {
      case 3:
        return 'Beginner';
      case 4:
        return 'Intermediate';
      case 5:
        return 'Advanced';
      case 6:
        return 'Expert';
      case 7:
        return 'Master';
      default:
        return 'Unknown';
    }
  };

  return (
    <>
      <Header />
      <div className="overflow-y-scroll min-h-screen bg-[rgb(18,18,18,0.9)] flex items-center justify-center w-full text-white">
        <div className='h-full w-[95%] flex flex-col justify-center items-center'>
          <label className='uppercase font-light tracking-widest text-2xl my-4'>Select Difficulty:</label>

          {difficulties.map((difficulty) => (
            <div
              key={difficulty}
              className={`w-[315px] relative m-2 border border-neutral-200 ${selectedDifficulty === difficulty ? 'bg-[#720E98]' : ''} rounded-sm transition-all transform duration-500 cursor-pointer`}
              onClick={() => setSelectedDifficulty(difficulty)}
            >
              {/* <div className='w-full text-2xl'>
                <h1 className='text-center uppercase font-light tracking-widest'>
                  {getDifficultyName(difficulty)}
                </h1>
              </div> */}
              <div
                  className='w-full h-full bg-cover opacity-60 hover:opacity-80 transition-all transform duration-500'
              >
              </div>
              <div className='absolute'>
                  <h1 className='tracking-widest text-2xl uppercase font-light'>{getDifficultyName(difficulty)}</h1>
              </div>
            </div>
          ))}
          <button onClick={handleStartGame}>Start Game</button>
        </div>
      </div>
    </>
  );
};

export default DifficultySelector;
