import React, { useState, useEffect } from 'react';

const shuffleArray = (array) => {
  const shuffledArray = [...array];
  for (let i = shuffledArray.length - 1; i > 0; i--) {
    const j = Math.floor(Math.random() * (i + 1));
    [shuffledArray[i], shuffledArray[j]] = [shuffledArray[j], shuffledArray[i]];
  }
  return shuffledArray;
};

const generateContentPairs = (difficulty) => {
  const symbols = ['A', 'B', 'C', 'D', 'E', 'F', 'G', 'H', 'I', 'J', 'K', 'L'];
  const cardCount = difficulty * difficulty;
  const pairsCount = cardCount / 2; // Since you want 2 matching pairs for each difficulty

  // Create an array with pairs of symbols
  let cards = symbols.slice(0, pairsCount).flatMap((symbol) => Array(2).fill(symbol));

  // Shuffle the cards
  cards = shuffleArray(cards);

  // Add letters for the back of each card
  const letters = 'ABCDEFGHIJKLMNOPQRSTUVWXYZ'.slice(0, cardCount);
  cards = cards.map((symbol, index) => ({ symbol, letter: letters[index] }));

  // Double the cards to create pairs
  cards = cards.concat(cards);

  // Shuffle the cards again
  return shuffleArray(cards);
};

const Card = ({ content, isFlipped, onClick }) => {
  return (
    <div
      className={`group rounded-xl h-24 w-24 m-4 [perspective:1000px]`}
      onClick={onClick}
    >
      <div className={`relative bg-red-500 h-full w-full items-center justify-center flex  rounded-xl shadow-xl transition-all text-slate-200 duration-500 [transform-style:preserve-3d] ${isFlipped ? 'group-hover:[transform:rotateY(180deg)]' : ''}`}>
        {isFlipped && <div className="absolute inset-0 w-full h-full rounded-xl items-center justify-center flex object-cover text-center text-slate-200 [transform-style:preserve-3d] [transform:rotateY(180deg)] [backface-visibility:hidden]">{content.letter}</div>}
        <div className={`absolute bg-red-500 inset-0 w-full h-full rounded-xl items-center justify-center flex object-cover text-center text-slate-200 [transform-style:preserve-3d] ${isFlipped ? '[transform:rotateY(180deg)] [backface-visibility:hidden]' : ''}`}>
          {isFlipped && content.symbol}
        </div>
      </div>
    </div>
  );
};

const MemoryGame = ({ difficulty }) => {
  const [cards, setCards] = useState([]);
  const [flippedIndexes, setFlippedIndexes] = useState([]);
  const [matchedPairs, setMatchedPairs] = useState([]);

  const handleCardClick = (id) => {
    if (flippedIndexes.length < 3 && !flippedIndexes.includes(id)) {
      setCards((prevCards) =>
        prevCards.map((row) =>
          row.map((card) =>
            card.id === id
              ? { ...card, isFlipped: !card.isFlipped }
              : card
          )
        )
      );
      setFlippedIndexes((prevIndexes) => [...prevIndexes, id]);
    }
  };

  useEffect(() => {
    const gridSize = difficulty; // Set the grid size (minimum 3, maximum 6)

    const contentPairs = generateContentPairs(difficulty);
    const shuffledContent = shuffleArray(contentPairs);

    const initialCards = Array.from({ length: gridSize }, (_, row) =>
      Array.from({ length: gridSize }, (_, col) => ({
        id: `${row}-${col}`,
        content: shuffledContent[row * gridSize + col],
        isFlipped: false,
      }))
    );

    setCards(initialCards);
  }, [difficulty]);

  useEffect(() => {
    if (flippedIndexes.length === 3) {
      const selectedCards = flippedIndexes.map((index) => {
        const [row, col] = index.split('-');
        return cards[row][col];
      });

      if (
        selectedCards.every(
          (card, _, array) => card.content.symbol === array[0].content.symbol
        )
      ) {
        setMatchedPairs((prevPairs) => [...prevPairs, selectedCards[0].content.symbol]);
      }

      setTimeout(() => {
        setFlippedIndexes([]);
        setCards((prevCards) =>
          prevCards.map((row) =>
            row.map((card) =>
              flippedIndexes.includes(card.id)
                ? { ...card, isFlipped: false }
                : card
            )
          )
        );
      }, 1000);
    }
  }, [flippedIndexes, cards]);

  useEffect(() => {
    if (matchedPairs.length === difficulty * difficulty / 3) {
      alert('Congratulations! You have won!');
    }
  }, [matchedPairs, difficulty]);

  return (
    <div className="flex flex-wrap justify-center">
      {cards.map((row, rowIndex) =>
        row.map((card, colIndex) => (
          <Card
            key={card.id}
            content={card.isFlipped ? card.content : ''}
            isFlipped={card.isFlipped}
            onClick={() => handleCardClick(card.id)}
          />
        ))
      )}
    </div>
  );
};

export default MemoryGame;
